package com.example.myapplication.Pages

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.input.KeyboardOptions
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import androidx.navigation.NavController
import com.example.myapplication.data.User

class AdminScreen {
    @Composable
    fun Content(navController: NavController) {
        val context = LocalContext.current
        val viewModel: AdminViewModel = viewModel(factory = AdminViewModel.Factory(context))
        var selectedTab by remember { mutableStateOf(0) }
        val tabTitles = listOf("Criar Usuário", "Lista de Usuários", "Adicionar Post")

        Column(modifier = Modifier.fillMaxSize().padding(16.dp)) {
            TabRow(selectedTabIndex = selectedTab) {
                tabTitles.forEachIndexed { index, title ->
                    Tab(selected = selectedTab == index,
                        onClick = { selectedTab = index },
                        text = { Text(title) })
                }
            }
            Spacer(modifier = Modifier.height(16.dp))

            when (selectedTab) {
                0 -> CreateUserTab(viewModel)
                1 -> UserListTab(viewModel)
                2 -> AddPostTab()
            }
        }
    }

    @Composable
    private fun CreateUserTab(viewModel: AdminViewModel) {
        var email by remember { mutableStateOf("") }
        var password by remember { mutableStateOf("") }
        var message by remember { mutableStateOf("") }

        Column {
            OutlinedTextField(
                value = email,
                onValueChange = { email = it },
                label = { Text("Email") },
                modifier = Modifier.fillMaxWidth()
            )
            Spacer(modifier = Modifier.height(8.dp))
            OutlinedTextField(
                value = password,
                onValueChange = { password = it },
                label = { Text("Senha") },
                keyboardOptions = KeyboardOptions.Default.copy(keyboardType = androidx.compose.ui.text.input.KeyboardType.Password),
                modifier = Modifier.fillMaxWidth()
            )
            Spacer(modifier = Modifier.height(8.dp))
            Button(onClick = {
                if (email.isNotBlank() && password.isNotBlank()) {
                    viewModel.createUser(email, password)
                    message = "Usuário criado!"
                    email = ""
                    password = ""
                } else {
                    message = "Preencha todos os campos."
                }
            }) {
                Text("Criar Usuário")
            }
            if (message.isNotEmpty()) {
                Spacer(modifier = Modifier.height(8.dp))
                Text(message)
            }
        }
    }

    @Composable
    private fun UserListTab(viewModel: AdminViewModel) {
        val users by viewModel.userList.collectAsState()

        LazyColumn {
            items(users.size) { index ->
                Text(text = users[index].email, modifier = Modifier.padding(8.dp))
                Divider()
            }
        }
    }

    @Composable
    private fun AddPostTab() {
        var imageUrl by remember { mutableStateOf("") }
        var postText by remember { mutableStateOf("") }
        var message by remember { mutableStateOf("") }

        Column {
            OutlinedTextField(
                value = imageUrl,
                onValueChange = { imageUrl = it },
                label = { Text("URL da Imagem") },
                modifier = Modifier.fillMaxWidth()
            )
            Spacer(modifier = Modifier.height(8.dp))
            OutlinedTextField(
                value = postText,
                onValueChange = { postText = it },
                label = { Text("Texto do Post") },
                modifier = Modifier.fillMaxWidth()
            )
            Spacer(modifier = Modifier.height(8.dp))
            Button(onClick = {
                if (imageUrl.isNotBlank() && postText.isNotBlank()) {
                    message = "Post adicionado!"
                } else {
                    message = "Preencha todos os campos."
                }
            }) {
                Text("Adicionar Post")
            }
            Spacer(modifier = Modifier.height(8.dp))
            if (message.isNotEmpty()) {
                Text(message)
            }
        }
    }
}

